      constructor(props) {
        super(props);
        this.state = {
          input_text: "Zoek je film !",
          zoek: "",
          movie: "",
          render: null
        }
        this.handleclick = this.handleclick.bind(this);
      }


      handleclick(event) {
        console.log(event.target.value)
        if (event.target.value === "zoek") {
          this.setState({
            zoek: "zoek",
            movie: this.film.value,
            render: true
          })
        } else if (event.target.value === "vind") {
          this.setState({
            zoek: "vind",
            movie: this.film.value,
            render: false

          })
