import React from 'react';
import './style/app.css'

class Search extends React.Component {

      constructor(props) {
        super(props);
        this.state = {
          input_text: "Zoek je film !",
          zoek: ""
        }
        this.handleclick = this.handleclick.bind(this);
      }

      realtime() {
        //Checkt states

      }


      searchmovie(movie) {
        console.log(movie + "A")
        fetch("localhost:9000/search" + movie)


        fetch("https://api.themoviedb.org/3/search/movie?api_key=ac5a895c793b78f0116f0b63423fc115&language=en-US&query=" + movie + "&page=1&include_adult=false")
        .then(res => res.json())
        .then((data) => {
          console.log(data)
        })
      }


      handleclick(event) {
        console.log(event.target.value)
        if (event.target.value === "zoek") {
          this.setState({
            zoek: "zoek"
          })          
          this.searchmovie(this.film.value)
        } else if (event.target.value === "vind") {
          this.setState({
            zoek: "vind"
          })
        }
      }

     render() {
        return(
          <div id="search" onChange={this.onChangeValue}>
           <input ref={(c) => this.film = c} id="title" name="title" placeholder={this.state.input_text} onChange={this.realtime}/><br/>
           <form>
             <label>
               zoeken
               <input type="radio" value="zoek" checked={this.state.zoek === "zoek"} onChange={this.handleclick}/>
             </label>
             <label>
               vinden
               <input type="radio" value="vind" checked={this.state.zoek === "vind"} onChange={this.handleclick}/>
             </label>
           </form>

          </div>
        )
      }
    }


  export default Search;
