import React from 'react';

class Search extends React.Component {

      constructor(props) {
        super(props);
        this.state = {
          input_text: "Zoek je film !",
          zoek: ""
        }
        this.handleclick = this.handleclick.bind(this);
      }
      handleclick(event) {
        console.log(event.target.value)
        if (event.target.value === "zoek") {
          this.setState({
            zoek: "zoek"
          })
          //let film = this.film;
          //console.log(film.value);
        } else if (event.target.value === "vind") {
          this.setState({
            zoek: "vind"
          })
        }
      }


     render() {
        return(
          <div onChange={this.onChangeValue}>
           <input ref={(c) => this.film = c} name="title" placeholder={this.state.input_text} onChange={this.state_handler}/><br/>
           <form>
             <label>
               zoeken
               <input type="radio" value="zoek" checked={this.state.zoek === "zoek"} onChange={this.handleclick}/>
             </label>
             <label>
               vinden
               <input type="radio" value="vind" checked={this.state.zoek === "vind"} onChange={this.handleclick}/>
             </label>
           </form>

          </div>
        )
      }
    }


  export default Search;
