import React from 'react'
import './style/display.css'


class Display extends React.Component { 


    findmovie(movie) {
        fetch("http://localhost:9000/find/" + movie)
        .then(res => res.json())
        .then((data) => {
          localStorage.setItem("movie_lists",JSON.stringify(data.name))
        })
      }
    render() {
      let movie_list = JSON.parse(localStorage.getItem("movie_lists"))      
      const list_movie = [];
     for (const [index,value] of movie_list.entries()) {
       list_movie.push(<div id="result" key={index}>{value}</div>)
     }
        return(
           <div className="display" id="display">
              {this.findmovie(this.props.movie)}

          </div>
         )}

}

export default Display
