import React from 'react'
import './style/display.css'


class Display extends React.Component { 

  constructor(props) {
    super(props)
    this.state = {
      movie: null,
      loading: true,
      render: this.props.search
    }
  }

  async componentDidMount() {

    switch (this.props.search) {
      case true:
        console.log("Search")
        break;
      case !true:
        const url = "http://localhost:9000/find/ " + this.props.movie;
        const response = await fetch(url);
        const data = await response.json();
        this.setState({movie: data.name,loading: false})
      default:
        break;
    }

