        super(props)
        this.movie_list = [];
    }
    findmovie(movie) {
        fetch("http://localhost:9000/find/" + movie)
        .then(res => res.json())
        .then((data) => {
          for (let i = 0; i < data.name.length; i++) {
            this.movie_list.push(data.name[i])
          }
          console.log(this.movie_list)
