import './style/display.css'


class Display extends React.Component { 

    constructor(props) {
      super(props)
      this.list_movie = [];

    }


    findmovie(movie) {
        fetch("http://localhost:9000/find/" + movie)
        .then(res => res.json())
        .then((data) => {
          //localStorage.setItem("movie_list",JSON.stringify(data.name))
           this.list_movie.push(data.name);
        })
      }
    render() {
      let movie_list = JSON.parse(localStorage.getItem("movie_list"))   


