import React from 'react'
import './style/display.css'


class Display extends React.Component { 

    constructor (props) {
      super(props)
      this.state = {
        name: []
      }
    }

    findmovie(movie) {
        fetch("http://localhost:9000/find/" + movie)
        .then(res => res.json())
        .then((data) => {
          this.setState({
            name: data.name
          })
          //localStorage.setItem("movie_list",JSON.stringify(data.name))
        })
      }
    render() {
      let movie_list = JSON.parse(localStorage.getItem("movie_list"))      
      const list_movie = [];
     for (const [index,value] of this.state.name.entries()) {
       list_movie.push(<div id="result" key={index}>{value}</div>)
     }


        return(
           <div className="display" id="display">
              {this.findmovie(this.props.movie)}
              {list_movie}
          </div>
         )}

}

export default Display
