import React from 'react'
import './style/display.css'


class Display extends React.Component { 

  constructor(props) {
    super(props)
    this.state = {
      movie: null,
      loading: true,
      movie_info: null
    }
  }

  async componentDidMount() {

    const urls = [
      "http://localhost:9000/find/ " + this.props.movie,
      "http://localhost:9000/info/ " + this.props.movie
    ]

    Promise.all(
      urls.map(url =>
        fetch(url)
          .then(([res1,res2]) => {
              console.log(res1)
              console.log(res2)
          })
      )
    );

    /*
    const url = "http://localhost:9000/find/ " + this.props.movie;
    const response = await fetch(url);
    const data = await response.json();
    this.setState({movie: data.name,loading: false})
    */
  }

    render() {      
      const list_movie = [];
      if (this.props.search) {
        if (this.state.movie != null) {
          for (const [index,value] of this.state.movie.entries()) {
            list_movie.push(<div id="result" key={index}>{value}</div>)
        } 
      }
      } else if (!this.props.search) {
       console.log(this.state.movie_info);
      } 
        return(
           <div className="display" id="display">
             {this.state.loading ? <div>Loading....</div> : list_movie}
          </div>
         )}

}

export default Display
