import React from 'react'
import './style/display.css'


class Display extends React.Component { 

    constructor (props) {
      super(props)
    }

    findmovie(movie) {
        let movielist;
        fetch("http://localhost:9000/find/" + movie)
        .then(res => res.json())
        .then((data) => {
          movie_list = data;
          //localStorage.setItem("movie_list",JSON.stringify(data.name))
          return movielist;
        })
      }
    render() {
      console.log(this.movie_list)
