import React from 'react'
import './style/display.css'


class Display extends React.Component { 

    constructor(props) {
        super(props)
         this.movie_list = [];
    }



    findmovie(movie) {
        fetch("http://localhost:9000/find/" + movie)
        .then(res => res.json())
        .then((data) => {
          this.movie_list.push.apply(this.movie_list,data.name);
          const list_movie = [];
          for (const [index,value] of this.movie_list.entries()) {
            list_movie.push(<div id="result" key={index}>{value}</div>)
          }          
        })
      }
    render() {
        return(
           <div className="display" id="display">
             {this.findmovie(this.props.movie)}
              {list_movie}
             {console.log(this.movie_list)}
          </div>
         )}

}

export default Display
